  "scripts": {
    "android": "react-native run-android --variant=developmentdebug --appIdSuffix=dev  --active-arch-only ",
    "android:prod": "react-native run-android --variant=productiondebug  --active-arch-only",
    "android:dev-release": "react-native run-android --variant=developmentrelease --appIdSuffix=dev --active-arch-only",
    "android:prod-release": "react-native run-android --variant=productionrelease --active-arch-only",
    "ios": "react-native run-ios --scheme=dim9000Dev",
    "ios:prod": "react-native run-ios",
    "iphone_se": "npx react-native run-ios --simulator='iPhone SE (3rd generation) (15.5)' --scheme=dim9000Dev",
    "start": "react-native start",
    "clean:android": "cd android && ./gradlew clean && cd ../",
    "build": "cd android && ./gradlew bundleRelease && cd ../",
    "clean:ios": "watchman watch-del-all && rm -rf node_modules/ && yarn cache clean && yarn install && cd ios && rm -rf Pods && rm -rf build && pod install && cd ..",
    "test": "jest",
    "lint": "yarn lint:js && yarn lint:css && yarn lint:types",
    "lint:js": "eslint --ext .ts --ext .tsx .",
    "lint:css": "stylelint \"./src/**/*.{ts,tsx}\"",
    "lint:types": "tsc",
    "lint:info": "eslint . --ext .js,.jsx,.ts,.tsx",
    "prepare": "husky install"
  },